const data = $input.item.json;
const amountFormatted = (data.amount / 100).toFixed(2);

const customerPortalLink = `https://billing.stripe.com/p/login/test_YOUR_LINK_HERE`;

return {
  json: {
    to: data.customer_email,
    subject: 'Action Required: Update Your Payment Method',
    html: `
      <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
        <h2>Hi ${data.customer_name},</h2>

        <p>We tried to process your payment of $${amountFormatted} but it failed because your card has expired.</p>

        <p>To avoid service interruption, please update your payment method:</p>

        <div style="text-align: center; margin: 30px 0;">
          <a href="${customerPortalLink}" style="background-color: #007bff; color: white; padding: 12px 30px; text-decoration: none; border-radius: 5px; display: inline-block;">Update Payment Method</a>
        </div>

        <p>We'll automatically retry your payment in 24 hours once you update your card.</p>

        <p>Questions? Reply to this email or contact support.</p>

        <p>Best regards,<br>Your Company Team</p>
      </div>
    `
  }
};

